mapper.insert:
INSERT INTO RESERVATION
 	 ( DATE 
 	 , TITLE
 	 , CINEMA
 	 , SEAT
 	 , P1
 	 , P2
 	 , P3
 	 , P_SUM
 	 , PAID_AMOUNT
 	 , PAY_METHOD
 	 , CARD_NAME
 	 , CARD_NUMBER
 	 , BUYER_TEL
 	 , SUCCESS
 	 , BUYER_NAME
 	 , MERCHANT_UID
 	 , SCR_IDX
 	 , S_TIME
 	 , LO_IDX
 	 , CI_IDX
 	 , SCH_IDX
 	 , MOV_TYPE
 	 , MOV_IDX
 	 , MEM_ID
 	 , PAY_TIME
 	 )  
VALUES 
	 ( LEFT(#{date} ,LENGTH(#{date}) - 4) 
	 , #{title}
	 , #{cinema}
	 , #{seat}
	 , #{p1}
	 , #{p2}
	 , #{p3}
	 , CAST(CAST(#{p1} AS UNSIGNED) + CAST(#{p2} AS UNSIGNED) + CAST(#{p3} AS UNSIGNED) AS CHAR(20))
	 , #{paidAmount}
	 , #{payMethod}
	 , #{cardName}
	 , #{cardNumber}
	 , #{buyerTel}
	 , #{success}
	 , #{buyerName}
	 , #{merchantUid}
	 , #{scrIdx}
	 , #{sTime}
	 , #{loIdx}
	 , #{ciIdx}
	 , #{schIdx}
	 , #{movType}
	 , #{movIdx}
	 , "test1"
	 , #{payTime}
	 )

mapper.select:
		SELECT SEAT
		  FROM RESERVATION
		 WHERE 1 = 1
		   AND SCR_IDX = #{scrIdx}
		   AND DATE_FORMAT(DATE, '%Y-%m-%d') = #{dateC}
		   AND S_TIME = #{sTime}
		   AND TITLE = #{title}
		   AND CINEMA = #{cinema}
 		   AND SUCCESS != 'false'       
		 UNION
		SELECT SEAT_C "SEAT"
		  FROM SEAT
		 WHERE 1 = 1
		   AND LO_IDX = #{loIdx}
		   AND CI_IDX = #{ciIdx}
		   AND MOV_TYPE = #{movType}
		   AND SCH_IDX = #{schIdx}
		   AND DATE_FORMAT(DATE_C, '%Y-%m-%d') = #{dateC}

SELECT COUNT(*)
  FROM SEAT
 WHERE 1 = 1
   AND LO_IDX = #{loIdx}
   AND CI_IDX = #{ciIdx}
   AND MOV_TYPE = #{movType}
   AND SCH_IDX = #{schIdx}
   AND DATE_C = #{dateC}
   AND (
   
   	   FIND_IN_SET(#{seat}, REPLACE(SEAT_C, ' ', '')) > 0
   
   	   )

mapper.insert:
INSERT INTO SEAT
     ( MEM_ID
     , LO_IDX
     , CI_IDX
     , MOV_TYPE
     , SCH_IDX
     , SEAT_C
     , DATE_C
     , INSERT_TIME
     )
VALUES
	 (
	   "test1"
	 , #{loIdx}
     , #{ciIdx}
	 , #{movType}
	 , #{schIdx}
	 , REPLACE(#{seatC}, ',', ', ')
	 , #{dateC}
	 , NOW()
	 )

mapper.delete:
DELETE S FROM SEAT S LEFT JOIN RESERVATION R ON S.MEM_ID = R.MEM_ID AND S.LO_IDX = R.LO_IDX AND S.CI_IDX = R.CI_IDX AND S.SCH_IDX = R.SCH_IDX AND S.MOV_TYPE = R.MOV_TYPE AND S.SEAT_C = R.SEAT WHERE 1 = 1 AND R.SEAT IS NULL AND R.SUCCESS != 'false'
AND S.MEM_ID = #{memId}
AND TIMESTAMPDIFF(SECOND, S.INSERT_TIME, NOW()) >= 45

mapper.select:
SELECT COUNT(*)
  FROM SEAT
 WHERE 1 = 1
   AND LO_IDX = #{loIdx}
   AND CI_IDX = #{ciIdx}
   AND MOV_TYPE = #{movType}
   AND DATE_C = #{dateC}
   AND SEAT_C = REPLACE(#{seatC}, ',', ', ')
