mapper.insert:
INSERT INTO RESERVATION
 	 ( DATE 
 	 , TITLE
 	 , CINEMA
 	 , SEAT
 	 , P1
 	 , P2
 	 , P3
 	 , P_SUM
 	 , PAID_AMOUNT
 	 , PAY_METHOD
 	 , CARD_NAME
 	 , CARD_NUMBER
 	 , BUYER_TEL
 	 , SUCCESS
 	 , BUYER_NAME
 	 , MERCHANT_UID
 	 , SCR_IDX
 	 , S_TIME
 	 )  
VALUES 
	 ( LEFT(#{date} ,LENGTH(#{date}) - 4) 
	 , #{title}
	 , #{cinema}
	 , #{seat}
	 , #{p1}
	 , #{p2}
	 , #{p3}
	 , CAST(CAST(#{p1} AS UNSIGNED) + CAST(#{p2} AS UNSIGNED) + CAST(#{p3} AS UNSIGNED) AS CHAR(20))
	 , #{paidAmount}
	 , #{payMethod}
	 , #{cardName}
	 , #{cardNumber}
	 , #{buyerTel}
	 , #{success}
	 , #{buyerName}
	 , #{merchantUid}
	 , #{scrIdx}
	 , #{sTime}
	 )

mapper.select:
SELECT SEAT
  FROM RESERVATION
 WHERE 1 = 1
   AND SCR_IDX = #{scrIdx}
   AND DATE = #{date}
   AND S_TIME = #{sTime}
   AND TITLE = #{title}
   AND CINEMA = #{cinema}
