mapper.insert:
INSERT INTO STORE_ITEM
	( ITEM_IDX
	, ITEM_NAME
	, ITEM_DATAIL
	, ITEM_PRICE
	, ITEM_IMAGE
	, ITEM_NUMBER
	, ITEM_DATE
	, CREATE_USER
	, CREATE_DATE
	, UPDATE_USER
	, UPDATE_DATE)
VALUES(
	  (SELECT *
		 FROM (SELECT IFNULL(MAX(CAST(ITEM_IDX as unsigned)), 0) + 1
				 FROM STORE_ITEM) A)
	, #{itemName}
	, #{itemDatail}
	, #{itemPrice}
	, #{itemImage}
	, #{itemNumber}
	, #{itemDate}
	, #{createUser}
	, #{createDate}
	, #{updateUser}
	, #{updateDate})

mapper.select:
select ITEM_IDX, ITEM_NAME, ITEM_DATAIL, format(ITEM_PRICE, 0) "ITEM_PRICE", ITEM_IMAGE, CREATE_USER, CREATE_DATE
  from STORE_ITEM
 where ITEM_IDX LIKE 'B%';

select ITEM_IDX, ITEM_NAME, ITEM_DATAIL, format(ITEM_PRICE, 0) "ITEM_PRICE", ITEM_IMAGE, CREATE_USER, CREATE_DATE
  from STORE_ITEM
 where ITEM_IDX LIKE 'S%';

select ITEM_IDX, ITEM_NAME, ITEM_DATAIL, format(ITEM_PRICE, 0) "ITEM_PRICE", ITEM_IMAGE, CREATE_USER, CREATE_DATE
  from STORE_ITEM
 where ITEM_IDX LIKE 'T%';
