mapper.insert:
INSERT INTO STORE_ITEM
	( ITEM_IDX
	, ITEM_NAME
	, ITEM_DETAIL
	, ITEM_PRICE
	, ITEM_IMAGE
	, ITEM_NUMBER
	, ITEM_DATE
	, CREATE_USER
	, CREATE_DATE
	, UPDATE_USER
	, UPDATE_DATE)
VALUES(
	  (SELECT *
		 FROM (SELECT IFNULL(MAX(CAST(ITEM_IDX as unsigned)), 0) + 1
				 FROM STORE_ITEM) A)
	, #{itemName}
	, #{itemDetail}
	, #{itemPrice}
	, #{itemImage}
	, #{itemNumber}
	, #{itemDate}
	, #{createUser}
	, #{createDate}
	, #{updateUser}
	, #{updateDate})

mapper.select:
 
		SELECT COUNT(*)
		FROM MEMBERS
		WHERE MEM_PHONE = #{phone}
